import React from "react";
import "./checkout-item.styles.scss";
import { connect } from "react-redux";
import { removeItem, increaseItem } from "../../redux/cart/cart-action";
import { addItemToCart } from "../../redux/cart/cart-utils";

const CheckoutItem = ({ cartItem, removeItem, increaseItem }) => {
  const { quantity, name, imageUrl, price } = cartItem;
  return (
    <div className="checkout-item">
      <img className="image-container" alt="item" src={imageUrl}></img>
      <span className="name">{name}</span>
      <span className="quantity">
        <div className="arrow">&#10094;</div>
        <span className="value">{quantity}</span>
        <div
          className="arrow"
          onClick={() => increaseItem(cartItems, cartItem)}
        >
          &#10095;
        </div>
      </span>
      <span className="price">{price}</span>
      <div className="remove-button" onClick={() => removeItem(cartItem)}>
        &#10005;
      </div>
    </div>
  );
};

const mapDispatchToProps = dispatch => ({
  removeItem: item => dispatch(removeItem(item)),
  increaseItem: item => dispatch(addItemToCart())
});

export default connect(
  null,
  mapDispatchToProps
)(CheckoutItem);
