import React from "react";
import "./checkout.styles.scss";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import {
  selectTotalCart,
  selectCartItems
} from "../../redux/cart/cart-selectors";
import CheckoutItem from "../../components/checkout-item/checkout-item.component";
import StripeCheckoutButton from "../../components/stripe-button/stripe-button.component";
import { CheckoutPageContainer } from "./checkout.styles";

const CheckoutPage = ({ cartItems, totalCart }) => (
  <CheckoutPageContainer>
    <div className="checkout-header">
      <div className="header-block">
        <span>Products</span>
      </div>
      <div className="header-block">
        <span>Description</span>
      </div>
      <div className="header-block">
        <span>Quantity</span>
      </div>
      <div className="header-block">
        <span>Price</span>
      </div>
      <div className="header-block">
        <span>Remove</span>
      </div>
    </div>
    {cartItems.length
      ? cartItems.map((item, index) => (
          <CheckoutItem key={index} cartItem={item} />
        ))
      : null}
    <div className="total">Total: â‚¬{totalCart}</div>
    <div className="test-warning">
      *Please use the following test credit card for payments*
      <br />
      4242 4242 4242 4242 - Exp: 01/20 - CVV: 123
    </div>
    <StripeCheckoutButton price={totalCart} />
  </CheckoutPageContainer>
);

const mapStateToProps = createStructuredSelector({
  cartItems: selectCartItems,
  totalCart: selectTotalCart
});

export default connect(mapStateToProps)(CheckoutPage);
