  const onToken = token => {
    console.log(token);
    alert("Payment Succesful!");
  };

  return (
    <div>
      <StripeCheckout
        label="Pay Now"
        name="CRWN Clothing"
        description={`Your total is â‚¬${price}`}
        amount={priceForStripe}
        currency="EUR"
        stripeKey={publishableKey}
        shippingAddress
        billingAddress
        token={onToken}
        alipay
        bitcoin
        allowRememberMe
      />
    </div>
  );
};

export default StripeCheckoutButton;
