import { removeItemFromCart } from "./cart-action";

export const addItemToCart = (cartItems, itemToBeAdded) => {
  const itemExistence = cartItems.find(item => item.id === itemToBeAdded.id);

  if (itemExistence) {
    return cartItems.map(cartItem =>
      cartItem.id === itemToBeAdded.id
        ? { ...cartItem, quantity: cartItem.quantity + 1 }
        : cartItem
    );
  }

  return [...cartItems, { ...itemToBeAdded, quantity: 1 }];
};

export const removeItem = (cartItems, itemToBeRemoved) => {
  const itemExistence = cartItems.find(item => item.id === itemToBeRemoved.id);

  if (itemExistence) {
    return cartItems.map(cartItem =>
      cartItem.id === itemToBeRemoved.id
        ? cartItem.quantity === 0
          ? removeItemFromCart
          : { ...cartItem, quantity: cartItem.quantity - 1 }
        : cartItem
    );
  }
};
