import React from "react";
import "./checkout-item.styles.scss";
import { connect } from "react-redux";
import {
  removeItemFromCart,
  addItem,
  removeItem
} from "../../redux/cart/cart-action";
import {
  CheckoutItemContainer,
  ImageContainer,
  ImageStyles,
  ItemDetails
} from "./checkout-item.styles";

const CheckoutItem = ({
  cartItem,
  removeItemFromCart,
  increaseItem,
  removeItem
}) => {
  const { quantity, name, imageUrl, price } = cartItem;
  return (
    <CheckoutItemContainer>
      <ImageContainer>
        <ImageStyles alt="item" src={imageUrl}></ImageStyles>
      </ImageContainer>

      <ItemDetails>{name}</ItemDetails>
      <span className="quantity">
        <div className="arrow" onClick={() => removeItem(cartItem)}>
          &#10094;
        </div>
        <span className="value">{quantity}</span>
        <div className="arrow" onClick={() => increaseItem(cartItem)}>
          &#10095;
        </div>
      </span>
      <span className="price">{price}</span>
      <div
        className="remove-button"
        onClick={() => removeItemFromCart(cartItem)}
      >
        &#10005;
      </div>
    </CheckoutItemContainer>
  );
};

const mapDispatchToProps = dispatch => ({
  removeItemFromCart: item => dispatch(removeItemFromCart(item)),
  increaseItem: item => dispatch(addItem(item)),
  removeItem: item => dispatch(removeItem(item))
});

export default connect(
  null,
  mapDispatchToProps
)(CheckoutItem);
