import { CartActionTypes } from "./cart-types.js";
import { addItemToCart, increaseItem } from "./cart-utils";

const ORIGIN_STATE = {
  hidden: true,
  cartItems: []
};

const cartReducer = (state = ORIGIN_STATE, action) => {
  switch (action.type) {
    case CartActionTypes.TOGGLE_CART_STATUS:
      return {
        ...state,
        hidden: !state.hidden
      };
    case CartActionTypes.ADD_ITEM:
      return {
        ...state,
        cartItems: addItemToCart(state.cartItems, action.payload)
      };
    case CartActionTypes.REMOVE_ITEM_FROM_CART:
      return {
        ...state,
        cartItems: state.cartItems.filter(
          cartItem => cartItem.id !== action.payload.id
        )
      };
    case CartActionTypes.ADD_ONE_ITEM:
      return {
        ...state,
        cartItems: increaseItem(action.payload)
      };
    default:
      return state;
  }
};

export default cartReducer;
